package org.firstinspires.ftc.teamcode.robot.robotClasses.Pathfinding;

public class Field {
    private final String halfFieldBorderWithSquare = "";
    private final String halfFieldBorder = "";
    private final String startFieldPosition = halfFieldBorderWithSquare;

    boolean[][] isOpen = new boolean[144][144];

    public Field() {
        for (int i = 0; i < 144; i++) {
            for (int j = 0; j < 144; j++) {
                isOpen[i][j] = startFieldPosition.charAt(i*144+j) == '0';
            }
        }
    }

    public void visualizeStartField(){
        String s = "";
        for (int i = 0; i < 144; i++) {
            for (int j = 0; j < 144; j++) {
                s+=startFieldPosition.charAt(i*144+j);
            }
            s+="\n";
        }
        System.out.println(s);
    }

    public boolean isOpenWithGlobalAt (int x, int y){
        return isOpen[getGridI(y)][getGridJ(x)];
    }

    public boolean isOpenWithGridAt (int i, int j){
        return isOpen[i][j];
    }

    public static int getGlobalX (int j){
        return j-72;
    }
    public static int getGlobalY (int i){
        return -(i-72);
    }
    public static int getGridI (int y){
        return -y+72;
    }
    public static int getGridJ (int x){
        return x+72;
    }

}
